// ===============================
// ðŸŽ¨ VISUAL BOTÃƒO
// ===============================
function disableButton() {
  startBtn.mouseEnabled = false;
  startBtn.alpha = 0.4;
}

function enableButton() {
  startBtn.mouseEnabled = true;
  startBtn.alpha = 1;
}

// ===============================
// ðŸš€ INIT
// ===============================
nameTxt.text = PLACEHOLDER;
disableButton();

// ===============================
// âŒ¨ï¸ INPUT TECLADO
// ===============================
window.addEventListener("keydown", handleKey);

function handleKey(e) {
  // Backspace
  if (e.key === "Backspace") {
    playerName = playerName.slice(0, -1);
  }
  // Letras, nÃºmeros e espaÃ§o
  else if (/^[a-zA-Z0-9 ]$/.test(e.key)) {
    if (playerName.length < MAX_CHARS) {
      playerName += e.key;
    }
  }

  updateText();
  validateName();
}

// ===============================
// ðŸ“ UPDATE TEXTO
// ===============================
function updateText() {
  nameTxt.text = playerName.length > 0 ? playerName : PLACEHOLDER;
}

// ===============================
// âœ… VALIDAÃ‡ÃƒO
// ===============================
function validateName() {
  if (playerName.length >= MIN_CHARS && playerName.length <= MAX_CHARS) {
    enableButton();
  } else {
    disableButton();
  }
}

// ===============================
// â–¶ï¸ BOTÃƒO PLAY (MENU â†’ QUIZ)
