// ===============================
// ðŸ”„ RESET DO JOGO
// ===============================
function resetGame() {
  root.score = 0;
  root.playerName = null;
  root.current = 0;
  root.selectedIndex = null;
  root.locked = false;

  if (root.userAnswers) {
    root.userAnswers.length = 0;
  }

  if (root.timerInterval) {
    clearInterval(root.timerInterval);
  }

  if (root.bgMusic) {
    root.bgMusic.stop();
    root.bgMusic = null;
  }
}

