// ===============================
// ðŸ“Š CARREGAR LEADERBOARD
// ===============================
fetch("leaderboard.js")
  .then(res => res.json())
  .then(data => {
    if (!data.length) {
      root.txtName.text = "Sem resultados ainda.";
      return;
    }

    let output = "";
    let rank = 1;

    data.forEach(entry => {
      output +=
        rank + ". " +
        entry.player +
        " ".repeat(30 - entry.player.length) +
        entry.score +
        "\n";
      rank++;
    });

    root.txtName.text = output;
  })
  .catch(err => {
    console.error(err);
    root.txtName.text = "Erro a carregar leaderboard.";
  });

